import React from "react";
import {
  View,
  Text,
  StyleSheet,
  Button,
  Image,
  TouchableOpacity,
  TouchableNativeFeedback,
  Platform,
} from "react-native";

import { useSelector, useDispatch } from "react-redux";
import { Ionicons } from "@expo/vector-icons";
import Colors from "../../constants/Colors";
import Card from "../UI/Card";

import { addToCart } from "../../store/actions/cart";
import { decrease } from "../../store/actions/cart";
import { increase } from "../../store/actions/cart";

const ProductItem = (props) => {
  const cartItem = useSelector((state) => state.cart.items);

  let quantity = 0;
  if (cartItem[props.id]) {
      quantity = cartItem[props.id].quantity;
    }

    const dispatch = useDispatch();

  let TouchableComp = TouchableOpacity;
  if (Platform.OS === "android" && Platform.Version >= 21) {
    TouchableComp = TouchableNativeFeedback;
  }

  return (
    <Card style={styles.product}>
      <View style={styles.touchable}>
        <TouchableComp useForeGround>
          <View>
            <View onPress={props.onSelect} style={styles.imageContainer}>
              <TouchableNativeFeedback onPress={props.onSelect}>
                <Image source={{ uri: props.image }} style={styles.image} />
              </TouchableNativeFeedback>
            </View>
            <View style={styles.details}>
              <Text onPress={props.onSelect} style={styles.title}>
                {props.title}
              </Text>
              <Text style={styles.price}>${props.price.toFixed(2)}</Text>
            </View>

            <View style={styles.action}>
              {/* {props.children} */}
              <TouchableOpacity>
                <Button
                  color={Colors.primary}
                  title="Details"
                  onPress={props.onSelect}
                />
              </TouchableOpacity>

              {quantity === 0 ? (
                <TouchableOpacity
                  onPress={() => {
                     dispatch(addToCart(props.product));
                  }}
                >
                  <Ionicons
                    name={
                      Platform.OS === "android" ? "md-basket" : "ios-basket"
                    }
                    size={40}
                    color={Colors.primary}
                  />
                </TouchableOpacity>
              ) : (
                <View style={{ flexDirection: "row", marginVertical: 5 }}>
                    <TouchableOpacity onPress={() => {

                      dispatch(decrease(props.product.id));
                     }}>
                    <Ionicons
                      name={
                        Platform.OS === "android"
                          ? "md-remove-circle"
                          : "ios-remove-circle"
                      }
                      size={34}
                      color={Colors.primary}
                    />
                  </TouchableOpacity>

                    <Text style={styles.quantity}> {quantity} </Text>
                    <TouchableOpacity onPress={() => { 
                       dispatch(increase(props.product.id));
                  }}>
                    <Ionicons
                      name={
                        Platform.OS === "android"
                          ? "md-add-circle"
                          : "ios-add-circle"
                      }
                      size={34}
                      color={Colors.primary}
                    />
                  </TouchableOpacity>
                </View>
              )}
            </View>
          </View>
        </TouchableComp>
      </View>
    </Card>
