import React from "react";
import {
  View,
  Text,
  Image,
  StyleSheet,
  TouchableOpacity,
  Platform,
} from "react-native";
import { Ionicons } from "@expo/vector-icons";
import Colors from "../../constants/Colors";
import Card from "../UI/Card";

const CartItem = (props) => {
  return (
    <Card style={styles.productMain}>
      <View style={styles.imageContainer}>
        <Image style={styles.image} source={{ uri: props.image }} />
      </View>
      <View style={styles.detailContainer}>
        <View style={{ overFlow: "hidden" }}>
          <Text numberOfLines={1} style={styles.text}>
            {props.title}
          </Text>
        </View>
        <Text style={styles.text}>à§³{props.amount.toFixed(2)}</Text>

        <View style={styles.action}>
          <TouchableOpacity onPress={props.onDecrease}>
            <Ionicons
              name={
                Platform.OS === "android"
                  ? "md-remove-circle"
                  : "ios-remove-circle"
              }
              size={28}
              color={Colors.primary}
            />
          </TouchableOpacity>

          <Text style={styles.quantity}>{props.quantity} </Text>
          <TouchableOpacity onPress={props.onIncrease}>
            <Ionicons
              name={
                Platform.OS === "android" ? "md-add-circle" : "ios-add-circle"
              }
              size={28}
              color={Colors.primary}
            />
          </TouchableOpacity>
          <TouchableOpacity onPress={props.onDelete} style={styles.touchable}>
            <Ionicons
              name={Platform.OS === "android" ? "md-trash" : "ios-trash"}
              size={30}
              color="red"
            />
          </TouchableOpacity>
        </View>
      </View>
    </Card>
  );
};

const styles = StyleSheet.create({
  productMain: {
    height: 110,
    flexDirection: "row",
    justifyContent: "flex-start",
    borderBottomColor: "gray",
    borderBottomWidth: 1,
    margin: 3,  
  },
  text: {
    color: "black",
    fontSize: 15,
    fontWeight: "bold",
    overflow: "hidden",
    width: "90%",
  },
  quantity: {
    fontFamily: "open-sans",
    color: "#888",
    fontSize: 18,
    marginHorizontal: 5,
    paddingHorizontal: 5,
  },
  imageContainer: {
    width: "35%",
    height: 110,
    padding: 3
  },
  image: {
    width: "100%",
    height: "95%",
    resizeMode: "contain",
    borderRadius: 5,
  },
  detailContainer: {
    justifyContent: "space-around",
    alignContent: "center",
    marginLeft: 20,
  },
  action: {
    flexDirection: "row",
    marginVertical: 5,
  },
  touchable: {
    flexDirection: "row",
    marginLeft: "30%",
    justifyContent: "space-between",
    alignItems: "flex-end",
  },
});

export default CartItem;
